installation.script:
#!/bin/bash

# Update system and install dependencies
apt update
apt install -y python3 python3-pip ffmpeg curl jq unzip

# Create app directory structure
mkdir -p /mnt/server/processor-voicesentinel
cd /mnt/server/processor-voicesentinel

# Get latest release version
LATEST_RELEASE=$(curl -s https://api.github.com/repos/jackh54/VoiceSentinel/releases/latest | jq -r '.tag_name')

# Download and extract release
wget -O processor.zip https://github.com/PandaDevv/VoiceSentinel/releases/download/${LATEST_RELEASE}/processor-voicesentinel.zip
unzip processor.zip
rm processor.zip

# Install Python dependencies
python -m pip install --upgrade pip
python -m pip install -r requirements.txt

# Create default config if it doesn't exist
if [ ! -f "config.json" ]; then
    echo '{
        "transcriber": {
            "type": "whisper",
            "model_name": "${MODEL_NAME}",
            "language": "en"
        },
        "server": {
            "host": "0.0.0.0",
            "port": ${SERVER_PORT},
            "workers": ${WORKERS},
            "auth": {
                "api_keys": [${API_KEYS}],
                "rate_limit": {
                    "authenticated": {
                        "window_seconds": 60,
                        "max_requests": 1000,
                        "block_duration_seconds": 300
                    },
                    "unauthenticated": {
                        "window_seconds": 60,
                        "max_requests": 10,
                        "block_duration_seconds": 1800
                    }
                }
            }
        },
        "processing": {
            "max_concurrent_jobs": ${WORKERS},
            "queue_warning_threshold": 200,
            "max_queue_size": 1000,
            "processing_timeout_seconds": 90,
            "retry_attempts": 2,
            "retry_delay_seconds": 1
        }
    }' > config.json
fi

# Set correct permissions
chown -R container:container /mnt/server
